func TestKeysharesJSON(t *testing.T) {
	keyshares := &KeySharesCLI{}
	err := json.Unmarshal([]byte(keysharesFixture), keyshares)
	require.NoError(t, err)
	b, err := json.Marshal(keyshares)
	require.NoError(t, err)
	require.JSONEq(t, keysharesFixture, string(b))
}

